<?php

$wmfthemecustomizer_apply = ot_get_option( 'wmfthemecustomizer_apply', '0');
$wmfthemecustomizer = ot_get_option( 'wmfthemecustomizer','0');

//Load default theme if new installation
if ( ! function_exists( 'mobili_theme_import_new' ) && $wmfthemecustomizer_apply == 0 && $wmfthemecustomizer == 0) {
	function mobili_theme_import_new() {
	  $mobili_style = '';
		
				
	  /* textarea value */
	  $options = isset( $mobili_style ) ? unserialize( ot_decode( $mobili_style ) ) : '';
	  
	  /* get settings array */
	  $settings = get_option( 'option_tree_settings' );
	  
	  /* has options */
	  if ( is_array( $options ) ) {
		
		/* validate options */
		if ( is_array( $settings ) ) {
		
		  foreach( $settings['settings'] as $setting ) {
		  
			if ( isset( $options[$setting['id']] ) ) {
			  
			  $content = ot_stripslashes( $options[$setting['id']] );
			  
			  $options[$setting['id']] = ot_validate_setting( $content, $setting['type'], $setting['id'] );
			  
			}
		  
		  }
		
		}
		
		/* execute the action hook and pass the theme options to it */
		do_action( 'ot_before_theme_options_save', $options );
	  
		/* update the option tree array */
		update_option( 'option_tree', $options );
				
	  }
	return false;
  }
  
  add_action( 'admin_init', 'mobili_theme_import_new', 3 );  
}

//Load existing settings
if ( ! function_exists( 'mobili_theme_import' ) && $wmfthemecustomizer_apply == 1 && $wmfthemecustomizer != 0) {
  
  function mobili_theme_import($wmfthemecustomizer) {
	$wmfthemecustomizer = ot_get_option( 'wmfthemecustomizer','0');
	
	if($wmfthemecustomizer == 1){ 
		$mobili_style = '';
	}elseif($wmfthemecustomizer == 2){ 
		$mobili_style = '';
	}elseif($wmfthemecustomizer == 3){ 
		$mobili_style = '';
	}elseif($wmfthemecustomizer == 4){ 
		$mobili_style = '';
	}elseif($wmfthemecustomizer == 5){ 
		$mobili_style = '';
	}elseif($wmfthemecustomizer == 6){ 
		$mobili_style = '';
	}elseif($wmfthemecustomizer == 7){ 
		$mobili_style = '';
	}elseif($wmfthemecustomizer == 8){ 
		$mobili_style = '';
	}else{
		$mobili_style = '';
	}
	 
    /* check and verify import theme options data nonce */
    if ( isset( $mobili_style ) ) {
            
      /* textarea value */
      $options = isset( $mobili_style ) ? unserialize( ot_decode( $mobili_style ) ) : '';
      
      /* get settings array */
      $settings = get_option( 'option_tree_settings' );
      
      /* has options */
      if ( is_array( $options ) ) {
        
        /* validate options */
        if ( is_array( $settings ) ) {
        
          foreach( $settings['settings'] as $setting ) {
          
            if ( isset( $options[$setting['id']] ) ) {
              
              $content = ot_stripslashes( $options[$setting['id']] );
              
              $options[$setting['id']] = ot_validate_setting( $content, $setting['type'], $setting['id'] );
              
            }
          
          }
        
        }
        
        /* execute the action hook and pass the theme options to it */
        do_action( 'ot_before_theme_options_save', $options );
      
        /* update the option tree array */
        update_option( 'option_tree', $options );
                
      }
      
      
    }
        
    return false;

  }
  

  add_action( 'admin_init', 'mobili_theme_import', 4 );  
}

?>